mapper.insert:
INSERT INTO tb_file_filter
(
    extension
    , type
    , enable
)
VALUES
(
    #{extension}
    , #{type}
    , #{enable}

)

mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM tb_file_filter
    WHERE extension = #{extension}
)

mapper.delete:
DELETE
FROM tb_file_filter
WHERE extension = #{extension}
    AND type = #{type}
